import { api } from "../utils/Api.js";
import { useState, useEffect } from "react";

function Main({ onEditProfile, onAddPlace, onEditAvatar }) {

  const [userName, setUserName] = useState("");
  const [userDescription, setUserDescription] = useState("");
  const [userAvatar, setUserAvatar] = useState("");

  const [cards, setCards] = useState([]);

  useEffect(() => {
    api.getUserInfo()
      .then((result) => {
        setUserName(result.name);
        setUserDescription(result.about);
        setUserAvatar(result.avatar);
      })
      .catch((err) =>
        console.log(err))

    api.getInitialCards()
      .then((result) => {
        setCards(result);
      })
      .catch((err) =>
        console.log(err))
  }, []);

  return (
    <main class="content">
      <section class="profile">

        <div class="profile__user-info">
          <button type="button" class="profile__avatar-button" onClick={onEditAvatar}>
            <img src={userAvatar} class="profile__avatar" alt="Аватар" />
          </button>
          <div class="profile__info">
            <h1 class="profile__name">{userName}</h1>
            <button type="button" class="profile__edit-button" onClick={onEditProfile}></button>
            <h2 class="profile__about">{userDescription}</h2>
          </div>
        </div>
        <button type="button" class="profile__add-button" onClick={onAddPlace}></button>
      </section>

      <div class="elements">
        {cards.map((card) => {
          <article className="element">
            <img src={card.link} className="element__img" alt="picture" />
            <div className="element__info">
              <h2 className="element__title">{card.name}</h2>
              <div className="element__container-like">
                <button type="button" className="element__like">{card.likes.length}</button>
                <div className="element__like-counter"></div>
              </div>
            </div>
            <button type="button" className="element__delete"></button>
          </article>
        })}
      </div>
    </main>
  );
}

export default Main;
