import Card from "./Card";
import Footer from "./Footer";
import Header from "./Header";
import ImagePopup from "./ImagePopup";
import Main from "./Main";
import PopupWithForm from "./PopupWithForm";
import { useState, useEffect } from "react";
import { api } from "../utils/Api.js";
import CurrentUserContext from "../contexts/CurrentUserContext.js";



function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isImagePopupOpen, setIsImagePopupOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState();

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);

  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
    handleImagePopupOpen();
  }

  function handleImagePopupOpen() {
    setIsImagePopupOpen(true);
  }

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard({});
    setIsImagePopupOpen(false);
  }

  const isAnyPopupOpened = isEditAvatarPopupOpen || isEditProfilePopupOpen || isAddPlacePopupOpen || isImagePopupOpen;

  useEffect(() => {
    function handleCloseByEsc(evt) {
      if (evt.key === "Escape") {
        closeAllPopups();
      }
    }

    if (isAnyPopupOpened) {
      document.addEventListener("keydown", handleCloseByEsc);
    }

    return () => {
      document.removeEventListener("keydown", handleCloseByEsc);
    };
  }, [isAnyPopupOpened]);

  useEffect(() => {
    api.getUserInfo()
      .then((result) => {
        setCurrentUser(result);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="page" >

      <Header />
      <Main
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
      />
      <Footer />
      <PopupWithForm
        title={"Обновить аватар"}
        name={"avatar"}
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      >
        {<div className="popup__input-field">
          <input
            type="url"
            className="popup__input"
            placeholder="Ссылка на картинку"
            name="avatar"
            id="input-avatar-link"
            required />
          <span className="popup__input-error input-avatar-link-error"></span>
        </div>}
      </PopupWithForm>

      <PopupWithForm
        title={"Редактировать профиль"}
        name={"edit-profile"}
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
      >
          <div className="popup__input-field">
            <input
              type="text"
              className="popup__input"
              placeholder="Имя"
              name="name"
              id="input-name"
              minLength="2"
              maxLength="40"
              required />
            <span className="popup__input-error input-name-error"></span>
          </div>
          <div className="popup__input-field">
            <input
              type="text"
              className="popup__input"
              placeholder="О себе"
              name="about"
              id="input-about"
              minLength="2"
              maxLength="200"
              required />
            <span className="popup__input-error input-about-error"></span>
          </div>
      </PopupWithForm>

      <PopupWithForm
        title={"Новое место"}
        name={"add-element"}
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      >
          <div className="popup__input-field">
            <input
              type="text"
              className="popup__input"
              placeholder="Название"
              name="name"
              id="input-element-title"
              minLength="2"
              maxLength="30"
              required />
            <span className="popup__input-error input-element-title-error"></span>
          </div>
          <div className="popup__input-field">
            <input
              type="url"
              className="popup__input"
              placeholder="Ссылка на картинку"
              name="link" id="input-element-link"
              required />
            <span className="popup__input-error input-element-link-error"></span>
          </div>
      </PopupWithForm>

      <PopupWithForm title={"Вы уверены?"} name={"_save-button"}>
        {<button className="popup__save-button" type="button">Да</button>}
      </PopupWithForm>

      <ImagePopup
        card={selectedCard}
        onClose={closeAllPopups}
        isOpen={isImagePopupOpen}
      />

    </div>
  );
}

export default App;
