function Main() {

  function handleEditAvatarClick () {
    document.querySelector('.popup_avatar').classList.add('popup_opened');
  }

  function handleEditProfileClick () {
    document.querySelector('.popup_edit-profile').classList.add('popup_opened');

  }
  function handleAddPlaceClick () {
    document.querySelector('.popup_add-element').classList.add('popup_opened');

  }

  return (
    <main class="content">
      <section class="profile">

        <div class="profile__user-info">
          <button type="button" class="profile__avatar-button">
            <img src="#" class="profile__avatar" alt="Аватар" />
          </button>
          <div class="profile__info">
            <h1 class="profile__name"></h1>
            <button type="button" class="profile__edit-button"></button>
            <h2 class="profile__about"></h2>
          </div>
        </div>
        <button type="button" class="profile__add-button"></button>
      </section>

      <div class="elements"></div>
    </main>
  );
}

export default Main;
